import React, { useState } from 'react';
import { Outlet ,Link, useNavigate} from 'react-router-dom';

const Section = () => {

    const allSections = ['HHAPL','HHIND','CNGRTS']
    const titleMapping = {
        HHAPL : 'Account Holder',
        HHIND : 'People',
        CNGRTS : 'Congratulations'
    }

    const navigate = useNavigate();
    const [section, setSection] = useState('HHAPL');
    const goNext = () => {
        const nextSectionIndex = allSections.findIndex(s => s=== section);
        setSection(allSections[nextSectionIndex]);
        navigate('/application/' + section);
    }
    

    return (
        <>
            <h1>{titleMapping[section]}</h1>
            <div>
                <Link to="/dasboard">
                    <a>&lt; Back to Dashboard</a>
                </Link>
            </div>
            <div className='section-margin'>
                <Outlet />
            </div>
            <button className='blue-button' onClick={goNext}>Next &gt;</button>
        </>
    )
}
export default Section;
