import React , { useEffect, useState }from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Header.css';
import logo from '../../assets/nysoh-logo.png';
import emitter from './EventEmitter';

const Header = ({ isLoggedIn, setIsLoggedIn }) => {

  const navigate = useNavigate();

  const [userId, setUserId] = useState('User');

  const handleSignOut = () => {
    setIsLoggedIn(false);
    // Optionally redirect to home or signin page
    setUserId(null);
    navigate('/');
  };

  useEffect(() => {
    const handler = msg => setUserId(msg);
    emitter.on('message', handler);

    return () => emitter.off('message', handler);
  }, []);

  return (
    <header className="header">
      <div className="login-button-div">
        <div className="header-logo" onClick={handleSignOut}>
          <img src={logo} alt="Logo" style={{ width: '250px', height: 'auto' }} />
        </div>
        <div className="login-button d-flex">
          {isLoggedIn ?
            <div>
              <span>{userId}</span>
              <button className='blue-button' onClick={handleSignOut}>Logout</button>
            </div> :
            <Link to="/login">
              <button className='blue-button'>Login</button>
            </Link>}

        </div>
      </div>
      {
        isLoggedIn ?
          <div className='blue-header'>
            <div>
              jlojo
            </div>
          </div> : null
      }

    </header>
  );
};

export default Header;
